import { Component } from '@angular/core';

@Component({
  selector: 'app-input-button',
  templateUrl: './input-button.component.html',
  styleUrls: ['./input-button.component.css']
})
export class InputButtonComponent {

  mylist:string[]=[];
  listchild=0;


addText(text:string,id:any){
  if(text.trim()!='')
  {
    this.listchild=this.listchild+1;
    this.mylist.push(text);
    if(this.listchild%5==0)
    {

      id.classList.add('blu');
    }

    // const Content = document.createTextNode(`${text}`);
    // document.getElementById('my-list')?.appendChild(document.createElement('li').appendChild(Content));


    // alert(text);
  }

}


}
<div class="container">

    <button  (click)="addText(inputbox.value)">Add</button>
    <div id="all-inputs">
    <p><label for="my-input">Enter text</label></p>
      <p class="input-field">
        <input 
        type="text" 
        name="" 
        id="my-input"
        #inputbox
        >
      </p>


      <ul  *ngFor="let newitem of mylist">
        <li #singlelist>{{newitem}}</li>

      </ul>



    </div>

</div>
