import { Component } from '@angular/core';

@Component({
  selector: 'app-toggle-button',
  templateUrl: './toggle-button.component.html',
  styleUrls: ['./toggle-button.component.css']
})
export class ToggleButtonComponent {




  togglePara(num:number){
    let mypara=document.getElementById('para-content');
    num=num+1;
    if(mypara.style.display=='block')
    mypara.style.display='none';
    else
    mypara.style.display='block';


  }

}

<div class="container">
    <button id="toggle-btn" (click)="togglePara(0)">Display details</button>
    <p id="para-content">
        Baa, baa black sheep
        Have you any wool
        Yes sir, yes sir
        Three bags full.
    </p>
</div>
