import { Component } from '@angular/core';

@Component({
  selector: 'app-input-button',
  templateUrl: './input-button.component.html',
  styleUrls: ['./input-button.component.css']
})
export class InputButtonComponent {

addText(text:string){
  if(text.trim()!='')
  {
    document.getElementById('allinputs')?.appendChild(document.createElement('p').createTextNode());
    alert(text);
  }

}


}
<div class="container">

    <button  (click)="addText(inputbox.value)">Add</button>
    <div #allinputs>
    <p><label for="my-input">Enter text</label></p>
      <p class="input-field">
        <input 
        type="text" 
        name="" 
        id="my-input"
        #inputbox
        >
      </p>

    </div>

</div>
