import { Component } from '@angular/core';

@Component({
  selector: 'app-toggle-button',
  templateUrl: './toggle-button.component.html',
  styleUrls: ['./toggle-button.component.css']
})
export class ToggleButtonComponent {

  togglePara(num:number,clickCount:number){
    num=num+1;

    if(num%2==0)
    {
      document.getElementById('para-content')?.classList.add('hide');
    }
    else
    document.getElementById('para-content')?.classList.remove('hide');


    document.querySelector('.total-clicks')?.classList.add('show');




  }

}
